            var url = "/bitrix/templates/webfly_san/ajax/favorites.php";
            var requestData = {};
            var elem = $(this).val();
            var elemVal = $(this).attr("elem-val");
            var that = $(this);
            if($(this).is(":checked")){
                requestData = {elemId:elem, action:"add"};
                $.post(url,requestData,function(d){
                    that.val(d);
                    countAnimate(_this);
                });
            }else{
