            var offsetX, offsetY;
            offsetX = Math.ceil($(this).offset().left);
            offsetY = Math.ceil($(this).offset().top);	//current button coordinates
            var virtBtn;
            if ($(this).is(\'.link-basket\')) {
                virtBtn = \'#virtual\';
            }
            else {
                virtBtn = "#virtual2";
            }
